import React, { Component } from 'react'

class App extends Component {
  constructor(props){
    super(props);

    this.state={
      newItem:"",
      list:[]
    }
  }
  render(){
    return (
      <div className="App">
        <div>
          Add a new task...
          <br/>
          <input 
            type="text"
            placeholder="Type your task here..."
            value={this.state.newItem}
            onChange={e => this.updateInput("newItem", e.target.value)}
          />
          <button
            onClick={() => this.addItem()}
          >
            Add
          </button>
        </div>
      </div>
    );
  }
}
export default App;
